CREATE TABLE tasks(tid INTEGER PRIMARY KEY NOT NULL, uid INTEGER NOT NULL, title TEXT, desc TEXT, color TEXT, gid INTEGER NOT NULL DEFAULT 0);
CREATE TABLE users(uid INTEGER PRIMARY KEY NOT NULL, uname TEXT, email TEXT, passwd TEXT, rname TEXT, token TEXT);
CREATE TABLE events(eid INTEGER PRIMARY KEY NOT NULL, tid INTEGER NOT NULL, uid INTEGER NOT NULL, start TEXT, end TEXT, allDay TEXT, desc TEXT);
CREATE TABLE groups(gid INTEGER PRIMARY KEY NOT NULL, owner INTEGER NOT NULL, settings INTEGER, name TEXT);
CREATE TABLE members(mid INTEGER PRIMARY KEY NOT NULL, gid INTEGER NOT NULL, uid INTEGER, perms INTEGER);
